var _ = require('lodash');

var Buff = function( stub ){
  // the name / type of this buff
  this.name = '';

  // value of the buff like
  this.value = 0;

  // attribute this buff modifies
  //  - body, mind, spirit, hp
  this.attribute = '';

  // number of encounters this buff should last
  this.duration = 0;

  if (_.isObject( stub ) ) {
    _.merge(this, stub);
  }
};

module.exports = Buff;