var _ = require('lodash');

var Buff = function( stub ){
  // value of the buff like
  this.amount = 0;

  // attribute this buff modifies
  //  - body, mind, spirit, hp
  this.attribute = '';

  // number of encounters this buff should last
  this.duration = 0;

  // accepts array like: [ 2, 'hp', 5 ]
  // which would provide +2 hp for 5 encounters
  if (_.isArray( stub )){
    this.amount = stub[0];
    this.attribute = stub[1];
    this.duration = stub[2];
  }
  else if (_.isObject( stub ) ) {
    _.merge(this, stub);
  }
};

module.exports = Buff;