/**
 * An effect is an object that can automatically modify the game state.
 * Generally used to deal damage, heal HP, or provide item drops
 *
 *
 * effect = {
 *   // (number) - the amount of "type" to perform
 *   amount: N,
 *
 *   // (string) - determines how the amount is applied
 *   type: ( heal | dmg | item | bonus | attack | specific item | output ),
 *
 *   // (string) - attribute the amount is applied to
 *   attribute_types: ( body | mind | spirit )
 * }
 */
var Effect = function( stub ){

  // TODO - too specific, amount is not a good word for this
  this.amount = 0;
  this.type = '';
  // TODO - this is a bit specific of a name
  this.attribute_types = '';

  // merge stub into object
  if ( typeof stub === 'object' ) {
    for (var key in stub) {
      this[key] = stub[key];
    }
  }
};

module.exports = Effect;