/**
 * An effect is an object that can automatically modify the game state.
 * Generally used to deal damage, heal HP, or provide item drops
 *
 *
 * effect = {
 *   // (number) - the value of "type" to perform
 *   value: N,
 *
 *   // (string) - determines how the value is applied
 *   type: ( heal | dmg | item | bonus | attack | specific item | output ),
 * }
 */
var _ = require('lodash');

var Effect = function(){

  // TODO - too specific, value is not a good word for this
  this.value = 0;
  this.type = '';
  // TODO - this is a bit specific of a name

};

Effect.create = function( stub ){
  var effect = new Effect();

  // merge stub into object
  if ( typeof stub === 'object' ) {
    _.merge( effect, stub );
  }

  return effect;
};


module.exports = Effect;